RED.demo = (function() {
    function init() {
        var userMenu = $('<li><a id="red-ui-header-button-user" class="button hide" href="manager.html"></a></li>')
            .prependTo(".red-ui-header-toolbar");
        $('<i class="fa fa-address-card-o"></i>').appendTo(userMenu.find("a"));

        RED.menu.init({
            id: "red-ui-header-button-user",
            options: []
        });

        userMenu.find("a").click(function(event) {
            // Ngăn chặn hành động mặc định của liên kết
            event.preventDefault();
            // Lấy đường dẫn từ thuộc tính href của thẻ <a>
            var url = $(this).attr("href");
            // Chuyển hướng sang trang mới
            window.location.href = url;
        });
    }

    return {
        init: init,
    };
})();
