const filterBodyRequest = (req, res, next) => {
  console.log("body: ", req.body)
  if (req.body) {
    console.log("body: ")
    if (req.body.password)
      req.body.password = require('bcryptjs').hashSync(req.body.password, 8);
  }
  next();
};

module.exports = {
  filterBodyRequest,
};
