const Sequelize = require('sequelize');
module.exports = function(sequelize, DataTypes) {
  return sequelize.define('flows', {
    id: {
      type: DataTypes.CHAR(36),
      allowNull: true,
      defaultValue: Sequelize.Sequelize.fn('uuid'),
      primaryKey: true
    },
    flow: {
      type: DataTypes.JSON,
      allowNull: true
    },
    rev: {
      type: DataTypes.STRING(255),
      allowNull: true
    },
    username: {
      type: DataTypes.STRING(255),
      allowNull: true
    },
    created_time: {
      type: DataTypes.DATE,
      allowNull: true
    },
    edited_time: {
      type: DataTypes.DATE,
      allowNull: true
    }
  }, {
    sequelize,
    tableName: 'flows',
    timestamps: false
  });
};
